00026 #include "../../MCAL/I2C/i2c.h"
00027 #include"eeprom_ext.h"
00028 
00029 unsigned char e2pext_r(unsigned int addr)
00030 {
00031   unsigned char ret;
00032   unsigned char ah;
00033   unsigned char al;
00034 
00035   ah=(addr&0x0100)>>8;
00036   al=addr&0x00FF;
00037 
00038   i2c_start();  
00039   if(ah)
00040   {
00041     i2c_wb(0xA2);
00042   }
00043   else
00044   {
00045     i2c_wb(0xA0);
00046   }
00047   i2c_wb(al);
00048 
00049   i2c_start();
00050   if(ah)
00051   {
00052     i2c_wb(0xA3);
00053   }
00054   else
00055   {
00056     i2c_wb(0xA1);
00057   }
00058   ret=i2c_rb(1);
00059   i2c_stop();
00060 
00061   return ret;   
00062 }
00063 
00064 
00065 void e2pext_w(unsigned int addr, unsigned char val)
00066 {
00067   unsigned int tmp;
00068   unsigned char ah;
00069   unsigned char al;
00070   unsigned char nt;
00071 
00072   tmp=val;
00073   ah=(addr&0x0100)>>8;
00074   al=addr&0x00FF;
00075   nt=0;
00076 
00077   do
00078   {
00079     i2c_start();
00080     if(ah)
00081     { 
00082       i2c_wb(0xA2);
00083     }
00084     else
00085     {
00086       i2c_wb(0xA0);
00087     }
00088     i2c_wb(al);
00089     i2c_wb(tmp);
00090     i2c_stop();
00091 
00092     nt++;
00093   }
00094   while((tmp != e2pext_r(addr))&&(nt < 10));
00095 }
